import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Star, 
  Target, 
  Play,
  Pause,
  RotateCcw,
  Maximize2,
  Minimize2,
  Search,
  X
} from 'lucide-react';
import Button from '../components/ui/Button';
import Card from '../components/ui/Card';
import Input from '../components/ui/Input';

interface CareerNode {
  id: string;
  title: string;
  level: 'internship' | 'entry' | 'mid' | 'senior' | 'leadership';
  description: string;
  skills: string[];
  salary: string;
  experience: string;
  isUnlocked: boolean;
  isCompleted: boolean;
  isCurrent: boolean;
  children: string[];
  parent?: string;
  progress: number;
  difficulty: 'Easy' | 'Medium' | 'Hard' | 'Expert';
  timeToComplete: string;
  prerequisites: string[];
}

interface CareerPath {
  id: string;
  name: string;
  description: string;
  icon: string;
  color: string;
  nodes: CareerNode[];
  totalNodes: number;
  completedNodes: number;
  progress: number;
}

const CareerTreePage: React.FC = () => {
  const [selectedPath, setSelectedPath] = useState<string>('software-engineering');
  const [selectedNode, setSelectedNode] = useState<CareerNode | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterLevel, setFilterLevel] = useState<string>('all');

  const careerPaths: CareerPath[] = [
    {
      id: 'software-engineering',
      name: 'Software Engineering',
      description: 'Build scalable applications and systems',
      icon: 'ðŸ’»',
      color: 'from-blue-500 to-cyan-500',
      totalNodes: 12,
      completedNodes: 3,
      progress: 25,
      nodes: [
        {
          id: 'intern-1',
          title: 'Software Engineering Intern',
          level: 'internship',
          description: 'Learn fundamentals of software development through hands-on projects',
          skills: ['Programming Basics', 'Git', 'Code Review', 'Testing'],
          salary: '$20-30/hour',
          experience: '0-1 years',
          isUnlocked: true,
          isCompleted: true,
          isCurrent: false,
          children: ['junior-1'],
          progress: 100,
          difficulty: 'Easy',
          timeToComplete: '3-6 months',
          prerequisites: ['Computer Science degree or equivalent']
        },
        {
          id: 'junior-1',
          title: 'Junior Software Engineer',
          level: 'entry',
          description: 'Develop features and fix bugs under mentorship',
          skills: ['React', 'Node.js', 'SQL', 'API Design'],
          salary: '$70,000 - $90,000',
          experience: '1-2 years',
          isUnlocked: true,
          isCompleted: true,
          isCurrent: false,
          children: ['mid-1', 'mid-2'],
          parent: 'intern-1',
          progress: 100,
          difficulty: 'Easy',
          timeToComplete: '1-2 years',
          prerequisites: ['Internship experience', 'Portfolio projects']
        },
        {
          id: 'mid-1',
          title: 'Software Engineer',
          level: 'mid',
          description: 'Lead feature development and mentor junior developers',
          skills: ['System Design', 'Microservices', 'Cloud Platforms', 'DevOps'],
          salary: '$100,000 - $140,000',
          experience: '3-5 years',
          isUnlocked: true,
          isCompleted: false,
          isCurrent: true,
          children: ['senior-1', 'senior-2'],
          parent: 'junior-1',
          progress: 60,
          difficulty: 'Medium',
          timeToComplete: '2-3 years',
          prerequisites: ['2+ years experience', 'Leadership skills']
        },
        {
          id: 'mid-2',
          title: 'Full Stack Engineer',
          level: 'mid',
          description: 'Specialize in both frontend and backend development',
          skills: ['React', 'Vue.js', 'Python', 'Django', 'AWS'],
          salary: '$95,000 - $130,000',
          experience: '3-4 years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: ['senior-3'],
          parent: 'junior-1',
          progress: 0,
          difficulty: 'Medium',
          timeToComplete: '2-3 years',
          prerequisites: ['Frontend & Backend experience']
        },
        {
          id: 'senior-1',
          title: 'Senior Software Engineer',
          level: 'senior',
          description: 'Architect complex systems and drive technical decisions',
          skills: ['Architecture', 'Performance', 'Security', 'Mentoring'],
          salary: '$140,000 - $180,000',
          experience: '5-8 years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: ['lead-1', 'lead-2'],
          parent: 'mid-1',
          progress: 0,
          difficulty: 'Hard',
          timeToComplete: '3-4 years',
          prerequisites: ['5+ years experience', 'System design skills']
        },
        {
          id: 'senior-2',
          title: 'Staff Engineer',
          level: 'senior',
          description: 'Lead cross-functional initiatives and set technical direction',
          skills: ['Technical Leadership', 'Strategy', 'Cross-team Collaboration'],
          salary: '$160,000 - $200,000',
          experience: '6-10 years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: ['lead-3'],
          parent: 'mid-1',
          progress: 0,
          difficulty: 'Hard',
          timeToComplete: '4-5 years',
          prerequisites: ['6+ years experience', 'Leadership experience']
        },
        {
          id: 'senior-3',
          title: 'Principal Engineer',
          level: 'senior',
          description: 'Define technical standards and drive innovation',
          skills: ['Innovation', 'Standards', 'Research', 'Industry Knowledge'],
          salary: '$180,000 - $220,000',
          experience: '8-12 years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: ['lead-4'],
          parent: 'mid-2',
          progress: 0,
          difficulty: 'Expert',
          timeToComplete: '5-6 years',
          prerequisites: ['8+ years experience', 'Industry recognition']
        },
        {
          id: 'lead-1',
          title: 'Engineering Manager',
          level: 'leadership',
          description: 'Lead engineering teams and drive product development',
          skills: ['Team Management', 'Product Strategy', 'Hiring', 'Budget Planning'],
          salary: '$180,000 - $250,000',
          experience: '8+ years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: [],
          parent: 'senior-1',
          progress: 0,
          difficulty: 'Expert',
          timeToComplete: '2-3 years',
          prerequisites: ['Management experience', 'Leadership skills']
        },
        {
          id: 'lead-2',
          title: 'Principal Engineer',
          level: 'leadership',
          description: 'Set technical vision and drive engineering excellence',
          skills: ['Technical Vision', 'Architecture', 'Mentoring', 'Innovation'],
          salary: '$200,000 - $300,000',
          experience: '10+ years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: [],
          parent: 'senior-1',
          progress: 0,
          difficulty: 'Expert',
          timeToComplete: '3-4 years',
          prerequisites: ['10+ years experience', 'Technical leadership']
        },
        {
          id: 'lead-3',
          title: 'Director of Engineering',
          level: 'leadership',
          description: 'Lead multiple engineering teams and drive organizational strategy',
          skills: ['Organizational Leadership', 'Strategy', 'Budget Management', 'Stakeholder Management'],
          salary: '$220,000 - $350,000',
          experience: '12+ years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: [],
          parent: 'senior-2',
          progress: 0,
          difficulty: 'Expert',
          timeToComplete: '4-5 years',
          prerequisites: ['12+ years experience', 'Management experience']
        },
        {
          id: 'lead-4',
          title: 'CTO',
          level: 'leadership',
          description: 'Set technical vision for the entire organization',
          skills: ['Strategic Vision', 'Technology Roadmap', 'Executive Leadership', 'Innovation'],
          salary: '$300,000+',
          experience: '15+ years',
          isUnlocked: false,
          isCompleted: false,
          isCurrent: false,
          children: [],
          parent: 'senior-3',
          progress: 0,
          difficulty: 'Expert',
          timeToComplete: '5+ years',
          prerequisites: ['15+ years experience', 'Executive experience']
        }
      ]
    },
    {
      id: 'data-science',
      name: 'Data Science',
      description: 'Extract insights from data to drive decisions',
      icon: 'ðŸ“Š',
      color: 'from-purple-500 to-pink-500',
      totalNodes: 10,
      completedNodes: 1,
      progress: 10,
      nodes: []
    },
    {
      id: 'product-management',
      name: 'Product Management',
      description: 'Lead product strategy and development',
      icon: 'ðŸš€',
      color: 'from-green-500 to-emerald-500',
      totalNodes: 8,
      completedNodes: 0,
      progress: 0,
      nodes: []
    }
  ];

  const currentPath = careerPaths.find(path => path.id === selectedPath);
  const filteredNodes = currentPath?.nodes.filter(node => {
    const matchesSearch = node.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         node.skills.some(skill => skill.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesLevel = filterLevel === 'all' || node.level === filterLevel;
    return matchesSearch && matchesLevel;
  }) || [];

  const getLevelColor = (level: string) => {
    const colors = {
      internship: 'from-green-400 to-emerald-500',
      entry: 'from-blue-400 to-cyan-500',
      mid: 'from-purple-400 to-pink-500',
      senior: 'from-orange-400 to-red-500',
      leadership: 'from-yellow-400 to-orange-500'
    };
    return colors[level as keyof typeof colors] || 'from-gray-400 to-gray-500';
  };

  const getDifficultyColor = (difficulty: string) => {
    const colors = {
      Easy: 'text-green-400',
      Medium: 'text-yellow-400',
      Hard: 'text-orange-400',
      Expert: 'text-red-400'
    };
    return colors[difficulty as keyof typeof colors] || 'text-gray-400';
  };

  const renderNode = (node: CareerNode, index: number) => {
    const isSelected = selectedNode?.id === node.id;
    
    return (
      <motion.div
        key={node.id}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: index * 0.1 }}
        className={`relative ${isSelected ? 'z-10' : ''}`}
      >
        <Card
          variant="liquid"
          className={`p-6 cursor-pointer transition-all duration-300 ${
            isSelected 
              ? 'ring-2 ring-blue-400 scale-105' 
              : 'hover:scale-105'
          } ${
            node.isCompleted 
              ? 'bg-gradient-to-br from-green-500/20 to-emerald-500/20 border-green-400/50' 
              : node.isCurrent 
                ? 'bg-gradient-to-br from-blue-500/20 to-cyan-500/20 border-blue-400/50' 
                : node.isUnlocked 
                  ? 'bg-white/5' 
                  : 'bg-white/5 opacity-50'
          }`}
          onClick={() => setSelectedNode(node)}
        >
          <div className="space-y-4">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <div className="flex items-center space-x-3 mb-2">
                  <h3 className="text-lg font-semibold text-white">
                    {node.title}
                  </h3>
                  <div className={`px-2 py-1 rounded-full text-xs font-medium bg-gradient-to-r ${getLevelColor(node.level)} text-white`}>
                    {node.level.charAt(0).toUpperCase() + node.level.slice(1)}
                  </div>
                </div>
                <p className="text-white/70 text-sm mb-3">
                  {node.description}
                </p>
              </div>
              {node.isCompleted && (
                <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                  <Star size={16} className="text-white" />
                </div>
              )}
            </div>

            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-white/50">Salary:</span>
                <p className="text-white font-medium">{node.salary}</p>
              </div>
              <div>
                <span className="text-white/50">Experience:</span>
                <p className="text-white font-medium">{node.experience}</p>
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span className="text-white/50">Progress</span>
                <span className="text-white font-medium">{node.progress}%</span>
              </div>
              <div className="w-full bg-white/10 rounded-full h-2">
                <div 
                  className={`h-full bg-gradient-to-r ${getLevelColor(node.level)} rounded-full transition-all duration-500`}
                  style={{ width: `${node.progress}%` }}
                />
              </div>
            </div>

            <div className="flex items-center justify-between text-sm">
              <div className={`font-medium ${getDifficultyColor(node.difficulty)}`}>
                {node.difficulty}
              </div>
              <div className="text-white/50">
                {node.timeToComplete}
              </div>
            </div>

            <div className="flex flex-wrap gap-1">
              {node.skills.slice(0, 3).map((skill) => (
                <span
                  key={skill}
                  className="px-2 py-1 bg-white/10 text-white/70 text-xs rounded"
                >
                  {skill}
                </span>
              ))}
              {node.skills.length > 3 && (
                <span className="px-2 py-1 bg-white/10 text-white/70 text-xs rounded">
                  +{node.skills.length - 3}
                </span>
              )}
            </div>
          </div>
        </Card>
      </motion.div>
    );
  };

  return (
    <div className={`min-h-screen pt-16 pb-20 ${isFullscreen ? 'fixed inset-0 z-50 bg-horizon-950' : ''}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-4xl font-bold text-white mb-2">
              Career <span className="gradient-text">Tree</span>
            </h1>
            <p className="text-xl text-white/70">
              Visualize your professional growth journey
            </p>
          </div>
          
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              onClick={() => setIsPlaying(!isPlaying)}
              icon={isPlaying ? <Pause size={20} /> : <Play size={20} />}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </Button>
            <Button
              variant="ghost"
              onClick={() => setIsFullscreen(!isFullscreen)}
              icon={isFullscreen ? <Minimize2 size={20} /> : <Maximize2 size={20} />}
            >
              {isFullscreen ? 'Exit' : 'Fullscreen'}
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <div className="space-y-6">
            {/* Path Selection */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Career Paths
              </h3>
              <div className="space-y-3">
                {careerPaths.map((path) => (
                  <button
                    key={path.id}
                    onClick={() => setSelectedPath(path.id)}
                    className={`w-full p-4 rounded-xl text-left transition-all duration-300 ${
                      selectedPath === path.id
                        ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 border border-blue-400/50'
                        : 'bg-white/5 hover:bg-white/10'
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="text-2xl">{path.icon}</div>
                      <div className="flex-1">
                        <h4 className="font-medium text-white">{path.name}</h4>
                        <p className="text-white/60 text-sm">{path.description}</p>
                      </div>
                    </div>
                    <div className="mt-2 flex items-center justify-between text-sm">
                      <span className="text-white/50">
                        {path.completedNodes}/{path.totalNodes} completed
                      </span>
                      <span className="text-white font-medium">{path.progress}%</span>
                    </div>
                    <div className="mt-2 w-full bg-white/10 rounded-full h-1">
                      <div 
                        className={`h-full bg-gradient-to-r ${path.color} rounded-full`}
                        style={{ width: `${path.progress}%` }}
                      />
                    </div>
                  </button>
                ))}
              </div>
            </Card>

            {/* Filters */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Filters
              </h3>
              <div className="space-y-4">
                <Input
                  placeholder="Search nodes..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  icon={<Search size={18} />}
                  variant="glass"
                />
                
                <div>
                  <label className="block text-sm font-medium text-white/90 mb-2">
                    Level
                  </label>
                  <select
                    value={filterLevel}
                    onChange={(e) => setFilterLevel(e.target.value)}
                    className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                  >
                    <option value="all">All Levels</option>
                    <option value="internship">Internship</option>
                    <option value="entry">Entry Level</option>
                    <option value="mid">Mid Level</option>
                    <option value="senior">Senior Level</option>
                    <option value="leadership">Leadership</option>
                  </select>
                </div>
              </div>
            </Card>

            {/* Progress Stats */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Your Progress
              </h3>
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Completed</span>
                  <span className="text-white font-semibold">
                    {currentPath?.completedNodes || 0}
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">In Progress</span>
                  <span className="text-white font-semibold">1</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Total</span>
                  <span className="text-white font-semibold">
                    {currentPath?.totalNodes || 0}
                  </span>
                </div>
              </div>
            </Card>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            {selectedNode ? (
              /* Node Detail View */
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="space-y-6"
              >
                <Card variant="liquid" className="p-8">
                  <div className="flex items-start justify-between mb-6">
                    <div>
                      <h2 className="text-3xl font-bold text-white mb-2">
                        {selectedNode.title}
                      </h2>
                      <p className="text-xl text-white/70 mb-4">
                        {selectedNode.description}
                      </p>
                      <div className="flex items-center space-x-4">
                        <div className={`px-3 py-1 rounded-full text-sm font-medium bg-gradient-to-r ${getLevelColor(selectedNode.level)} text-white`}>
                          {selectedNode.level.charAt(0).toUpperCase() + selectedNode.level.slice(1)} Level
                        </div>
                        <div className={`px-3 py-1 rounded-full text-sm font-medium ${getDifficultyColor(selectedNode.difficulty)} bg-white/10`}>
                          {selectedNode.difficulty}
                        </div>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      onClick={() => setSelectedNode(null)}
                    >
                      <X size={20} />
                    </Button>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Key Information
                        </h3>
                        <div className="space-y-3">
                          <div className="flex justify-between">
                            <span className="text-white/70">Salary Range:</span>
                            <span className="text-white font-medium">{selectedNode.salary}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-white/70">Experience Required:</span>
                            <span className="text-white font-medium">{selectedNode.experience}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-white/70">Time to Complete:</span>
                            <span className="text-white font-medium">{selectedNode.timeToComplete}</span>
                          </div>
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Required Skills
                        </h3>
                        <div className="flex flex-wrap gap-2">
                          {selectedNode.skills.map((skill) => (
                            <span
                              key={skill}
                              className="px-3 py-1 bg-white/10 text-white/80 text-sm rounded-lg"
                            >
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>

                    <div className="space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Progress
                        </h3>
                        <div className="space-y-2">
                          <div className="flex justify-between text-sm">
                            <span className="text-white/70">Completion</span>
                            <span className="text-white font-medium">{selectedNode.progress}%</span>
                          </div>
                          <div className="w-full bg-white/10 rounded-full h-3">
                            <div 
                              className={`h-full bg-gradient-to-r ${getLevelColor(selectedNode.level)} rounded-full transition-all duration-500`}
                              style={{ width: `${selectedNode.progress}%` }}
                            />
                          </div>
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Prerequisites
                        </h3>
                        <ul className="space-y-2">
                          {selectedNode.prerequisites.map((prereq, index) => (
                            <li key={index} className="flex items-center space-x-2 text-white/70">
                              <div className="w-1.5 h-1.5 bg-white/50 rounded-full" />
                              <span>{prereq}</span>
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div className="pt-4">
                        <Button className="w-full" size="lg">
                          Start Learning Path
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              </motion.div>
            ) : (
              /* Tree View */
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <h2 className="text-2xl font-bold text-white">
                    {currentPath?.name} Career Tree
                  </h2>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      icon={<RotateCcw size={16} />}
                    >
                      Reset View
                    </Button>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                  <AnimatePresence>
                    {filteredNodes.map((node, index) => renderNode(node, index))}
                  </AnimatePresence>
                </div>

                {filteredNodes.length === 0 && (
                  <Card variant="glass" className="p-12 text-center">
                    <div className="w-16 h-16 bg-white/10 rounded-2xl flex items-center justify-center mx-auto mb-4">
                      <Target size={32} className="text-white/50" />
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">
                      No nodes found
                    </h3>
                    <p className="text-white/70">
                      Try adjusting your search or filter criteria
                    </p>
                  </Card>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CareerTreePage;
