import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Star, 
  Target, 
  Play,
  Pause,
  RotateCcw,
  Maximize2,
  Minimize2,
  Search,
  X,
  ExternalLink,
  Clock,
  MapPin,
  Briefcase,
  GraduationCap,
  Zap,
  TrendingUp,
  Users,
  Award,
  ChevronRight,
  ChevronDown,
  Loader2
} from 'lucide-react';
import Button from '../components/ui/Button';
import Card from '../components/ui/Card';
import Input from '../components/ui/Input';

interface Opportunity {
  title: string;
  url?: string;
  snippet?: string;
  source_type?: string;
  provenance: string[];
  confidence: number;
}

interface Stage {
  id: string;
  name: string;
  description?: string;
  eta_months?: number;
  nsqf_level?: number;
  skill_requirements: string[];
  top_opportunities: Opportunity[];
  provenance: string[];
}

interface PathBranch {
  id: string;
  title: string;
  summary: string;
  fit_score: number;
  confidence: number;
  stages: Stage[];
}

interface CareerTree {
  user_id: string;
  generated_at: string;
  domain_focus: string[];
  paths: PathBranch[];
  provenance: string[];
  confidence: number;
}

const CareerTreePage: React.FC = () => {
  const [careerTree, setCareerTree] = useState<CareerTree | null>(null);
  const [selectedPath, setSelectedPath] = useState<string | null>(null);
  const [selectedStage, setSelectedStage] = useState<Stage | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [expandedPaths, setExpandedPaths] = useState<Set<string>>(new Set());

  const generateCareerTree = async () => {
    setIsGenerating(true);
    try {
      const response = await fetch('http://localhost:8000/careertree/generate/x', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error('Failed to generate career tree');
      }
      
      const data = await response.json();
      setCareerTree(data.result.tree);
      setSelectedPath(data.result.tree.paths[0]?.id || null);
    } catch (error) {
      console.error('Error generating career tree:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  const togglePathExpansion = (pathId: string) => {
    setExpandedPaths(prev => {
      const newSet = new Set(prev);
      if (newSet.has(pathId)) {
        newSet.delete(pathId);
      } else {
        newSet.add(pathId);
      }
      return newSet;
    });
  };

  const getPathColor = (index: number) => {
    const colors = [
      'from-blue-500 to-cyan-500',
      'from-purple-500 to-pink-500',
      'from-green-500 to-emerald-500',
      'from-orange-500 to-red-500',
      'from-yellow-500 to-orange-500'
    ];
    return colors[index % colors.length];
  };

  const getStageIcon = (stageName: string) => {
    const name = stageName.toLowerCase();
    if (name.includes('intern') || name.includes('internship')) return <GraduationCap size={16} />;
    if (name.includes('master') || name.includes('phd') || name.includes('degree')) return <Award size={16} />;
    if (name.includes('job') || name.includes('engineer') || name.includes('developer')) return <Briefcase size={16} />;
    if (name.includes('lead') || name.includes('manager') || name.includes('director')) return <Users size={16} />;
    return <Target size={16} />;
  };

  const getFitScoreColor = (score: number) => {
    if (score >= 0.8) return 'text-green-400';
    if (score >= 0.6) return 'text-yellow-400';
    if (score >= 0.4) return 'text-orange-400';
    return 'text-red-400';
  };

  const getConfidenceColor = (confidence: number) => {
    if (confidence >= 0.8) return 'text-green-400';
    if (confidence >= 0.6) return 'text-yellow-400';
    if (confidence >= 0.4) return 'text-orange-400';
    return 'text-red-400';
  };

  const renderPathBranch = (path: PathBranch, index: number) => {
    const isExpanded = expandedPaths.has(path.id);
    const isSelected = selectedPath === path.id;
    const pathColor = getPathColor(index);
    
    return (
      <motion.div
        key={path.id}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: index * 0.1 }}
        className="relative"
      >
        {/* Path Header */}
        <motion.div
          className={`p-6 rounded-2xl cursor-pointer transition-all duration-300 ${
            isSelected 
              ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 border-2 border-blue-400/50' 
              : 'bg-white/5 hover:bg-white/10 border border-white/10'
          }`}
          onClick={() => {
            setSelectedPath(path.id);
            togglePathExpansion(path.id);
          }}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <div className="flex items-center space-x-3 mb-2">
                <h3 className="text-xl font-bold text-white">{path.title}</h3>
                <div className={`px-3 py-1 rounded-full text-sm font-medium bg-gradient-to-r ${pathColor} text-white`}>
                  {Math.round(path.fit_score * 100)}% fit
                </div>
              </div>
              <p className="text-white/70 mb-3">{path.summary}</p>
              <div className="flex items-center space-x-4 text-sm">
                <div className="flex items-center space-x-1">
                  <TrendingUp size={16} className="text-white/50" />
                  <span className={`font-medium ${getFitScoreColor(path.fit_score)}`}>
                    {Math.round(path.fit_score * 100)}% Match
                  </span>
                </div>
                <div className="flex items-center space-x-1">
                  <Zap size={16} className="text-white/50" />
                  <span className={`font-medium ${getConfidenceColor(path.confidence)}`}>
                    {Math.round(path.confidence * 100)}% Confidence
                  </span>
                </div>
                <div className="flex items-center space-x-1">
                  <Target size={16} className="text-white/50" />
                  <span className="text-white/70">{path.stages.length} stages</span>
                </div>
              </div>
            </div>
            <motion.div
              animate={{ rotate: isExpanded ? 90 : 0 }}
              transition={{ duration: 0.3 }}
            >
              <ChevronRight size={24} className="text-white/50" />
            </motion.div>
          </div>
        </motion.div>

        {/* Stages */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.4 }}
              className="mt-4 ml-8 space-y-4"
            >
              {path.stages.map((stage, stageIndex) => (
                <motion.div
                  key={stage.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.4, delay: stageIndex * 0.1 }}
                  className="relative"
                >
                  {/* Connection Line */}
                  <div className="absolute -left-6 top-8 w-6 h-px bg-gradient-to-r from-white/30 to-transparent" />
                  <div className="absolute -left-6 top-8 w-px h-8 bg-gradient-to-b from-white/30 to-transparent" />
                  
                  {/* Stage Node */}
                  <motion.div
                    className={`p-4 rounded-xl cursor-pointer transition-all duration-300 ${
                      selectedStage?.id === stage.id
                        ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 border-2 border-blue-400/50 scale-105'
                        : 'bg-white/5 hover:bg-white/10 border border-white/10'
                    }`}
                    onClick={() => setSelectedStage(stage)}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                        {getStageIcon(stage.name)}
                      </div>
                      <div className="flex-1">
                        <h4 className="font-semibold text-white">{stage.name}</h4>
                        {stage.description && (
                          <p className="text-white/70 text-sm mt-1">{stage.description}</p>
                        )}
                        <div className="flex items-center space-x-4 mt-2 text-xs">
                          {stage.eta_months && (
                            <div className="flex items-center space-x-1">
                              <Clock size={12} className="text-white/50" />
                              <span className="text-white/70">{stage.eta_months} months</span>
                            </div>
                          )}
                          <div className="flex items-center space-x-1">
                            <Target size={12} className="text-white/50" />
                            <span className="text-white/70">{stage.top_opportunities.length} opportunities</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                </motion.div>
              ))}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    );
  };

  return (
    <div className={`min-h-screen pt-16 pb-20 ${isFullscreen ? 'fixed inset-0 z-50 bg-horizon-950' : ''}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-4xl font-bold text-white mb-2">
              Career <span className="gradient-text">Tree</span>
            </h1>
            <p className="text-xl text-white/70">
              Visualize your professional growth journey
            </p>
          </div>
          
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              onClick={() => setIsPlaying(!isPlaying)}
              icon={isPlaying ? <Pause size={20} /> : <Play size={20} />}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </Button>
            <Button
              variant="ghost"
              onClick={() => setIsFullscreen(!isFullscreen)}
              icon={isFullscreen ? <Minimize2 size={20} /> : <Maximize2 size={20} />}
            >
              {isFullscreen ? 'Exit' : 'Fullscreen'}
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <div className="space-y-6">
            {/* Path Selection */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Career Paths
              </h3>
              <div className="space-y-3">
                {careerPaths.map((path) => (
                  <button
                    key={path.id}
                    onClick={() => setSelectedPath(path.id)}
                    className={`w-full p-4 rounded-xl text-left transition-all duration-300 ${
                      selectedPath === path.id
                        ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 border border-blue-400/50'
                        : 'bg-white/5 hover:bg-white/10'
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="text-2xl">{path.icon}</div>
                      <div className="flex-1">
                        <h4 className="font-medium text-white">{path.name}</h4>
                        <p className="text-white/60 text-sm">{path.description}</p>
                      </div>
                    </div>
                    <div className="mt-2 flex items-center justify-between text-sm">
                      <span className="text-white/50">
                        {path.completedNodes}/{path.totalNodes} completed
                      </span>
                      <span className="text-white font-medium">{path.progress}%</span>
                    </div>
                    <div className="mt-2 w-full bg-white/10 rounded-full h-1">
                      <div 
                        className={`h-full bg-gradient-to-r ${path.color} rounded-full`}
                        style={{ width: `${path.progress}%` }}
                      />
                    </div>
                  </button>
                ))}
              </div>
            </Card>

            {/* Filters */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Filters
              </h3>
              <div className="space-y-4">
                <Input
                  placeholder="Search nodes..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  icon={<Search size={18} />}
                  variant="glass"
                />
                
                <div>
                  <label className="block text-sm font-medium text-white/90 mb-2">
                    Level
                  </label>
                  <select
                    value={filterLevel}
                    onChange={(e) => setFilterLevel(e.target.value)}
                    className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                  >
                    <option value="all">All Levels</option>
                    <option value="internship">Internship</option>
                    <option value="entry">Entry Level</option>
                    <option value="mid">Mid Level</option>
                    <option value="senior">Senior Level</option>
                    <option value="leadership">Leadership</option>
                  </select>
                </div>
              </div>
            </Card>

            {/* Progress Stats */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Your Progress
              </h3>
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Completed</span>
                  <span className="text-white font-semibold">
                    {currentPath?.completedNodes || 0}
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">In Progress</span>
                  <span className="text-white font-semibold">1</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Total</span>
                  <span className="text-white font-semibold">
                    {currentPath?.totalNodes || 0}
                  </span>
                </div>
              </div>
            </Card>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            {selectedNode ? (
              /* Node Detail View */
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="space-y-6"
              >
                <Card variant="liquid" className="p-8">
                  <div className="flex items-start justify-between mb-6">
                    <div>
                      <h2 className="text-3xl font-bold text-white mb-2">
                        {selectedNode.title}
                      </h2>
                      <p className="text-xl text-white/70 mb-4">
                        {selectedNode.description}
                      </p>
                      <div className="flex items-center space-x-4">
                        <div className={`px-3 py-1 rounded-full text-sm font-medium bg-gradient-to-r ${getLevelColor(selectedNode.level)} text-white`}>
                          {selectedNode.level.charAt(0).toUpperCase() + selectedNode.level.slice(1)} Level
                        </div>
                        <div className={`px-3 py-1 rounded-full text-sm font-medium ${getDifficultyColor(selectedNode.difficulty)} bg-white/10`}>
                          {selectedNode.difficulty}
                        </div>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      onClick={() => setSelectedNode(null)}
                    >
                      <X size={20} />
                    </Button>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Key Information
                        </h3>
                        <div className="space-y-3">
                          <div className="flex justify-between">
                            <span className="text-white/70">Salary Range:</span>
                            <span className="text-white font-medium">{selectedNode.salary}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-white/70">Experience Required:</span>
                            <span className="text-white font-medium">{selectedNode.experience}</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-white/70">Time to Complete:</span>
                            <span className="text-white font-medium">{selectedNode.timeToComplete}</span>
                          </div>
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Required Skills
                        </h3>
                        <div className="flex flex-wrap gap-2">
                          {selectedNode.skills.map((skill) => (
                            <span
                              key={skill}
                              className="px-3 py-1 bg-white/10 text-white/80 text-sm rounded-lg"
                            >
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>

                    <div className="space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Progress
                        </h3>
                        <div className="space-y-2">
                          <div className="flex justify-between text-sm">
                            <span className="text-white/70">Completion</span>
                            <span className="text-white font-medium">{selectedNode.progress}%</span>
                          </div>
                          <div className="w-full bg-white/10 rounded-full h-3">
                            <div 
                              className={`h-full bg-gradient-to-r ${getLevelColor(selectedNode.level)} rounded-full transition-all duration-500`}
                              style={{ width: `${selectedNode.progress}%` }}
                            />
                          </div>
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-white mb-3">
                          Prerequisites
                        </h3>
                        <ul className="space-y-2">
                          {selectedNode.prerequisites.map((prereq, index) => (
                            <li key={index} className="flex items-center space-x-2 text-white/70">
                              <div className="w-1.5 h-1.5 bg-white/50 rounded-full" />
                              <span>{prereq}</span>
                            </li>
                          ))}
                        </ul>
                      </div>

                      <div className="pt-4">
                        <Button className="w-full" size="lg">
                          Start Learning Path
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              </motion.div>
            ) : (
              /* Tree View */
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <h2 className="text-2xl font-bold text-white">
                    {currentPath?.name} Career Tree
                  </h2>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      icon={<RotateCcw size={16} />}
                    >
                      Reset View
                    </Button>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                  <AnimatePresence>
                    {filteredNodes.map((node, index) => renderNode(node, index))}
                  </AnimatePresence>
                </div>

                {filteredNodes.length === 0 && (
                  <Card variant="glass" className="p-12 text-center">
                    <div className="w-16 h-16 bg-white/10 rounded-2xl flex items-center justify-center mx-auto mb-4">
                      <Target size={32} className="text-white/50" />
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">
                      No nodes found
                    </h3>
                    <p className="text-white/70">
                      Try adjusting your search or filter criteria
                    </p>
                  </Card>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CareerTreePage;
