import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Search, 
  Filter, 
  MapPin, 
  Clock, 
  Heart, 
  ExternalLink,
  Briefcase,
  TrendingUp,
  Users,
  DollarSign
} from 'lucide-react';
import Button from '../components/ui/Button';
import Input from '../components/ui/Input';
import Card from '../components/ui/Card';

interface Job {
  id: string;
  title: string;
  company: string;
  location: string;
  type: 'Full-time' | 'Part-time' | 'Contract' | 'Internship';
  salary?: string;
  posted: string;
  description: string;
  skills: string[];
  matchScore: number;
  isLiked: boolean;
  applyLink: string;
  companyLogo?: string;
}

const JobListingsPage: React.FC = () => {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [filteredJobs, setFilteredJobs] = useState<Job[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilters, setSelectedFilters] = useState({
    type: '',
    location: '',
    salary: '',
    skills: [] as string[],
  });
  const [showFilters, setShowFilters] = useState(false);
  const [sortBy, setSortBy] = useState('relevance');
  const [loading, setLoading] = useState(true);

  // Mock data
  useEffect(() => {
    const mockJobs: Job[] = [
      {
        id: '1',
        title: 'Senior Software Engineer',
        company: 'Google',
        location: 'Mountain View, CA',
        type: 'Full-time',
        salary: '$150,000 - $200,000',
        posted: '2 days ago',
        description: 'We are looking for a Senior Software Engineer to join our team and help build the next generation of web applications.',
        skills: ['React', 'TypeScript', 'Node.js', 'Python', 'AWS'],
        matchScore: 95,
        isLiked: false,
        applyLink: 'https://careers.google.com/jobs/results/123456',
      },
      {
        id: '2',
        title: 'Machine Learning Engineer',
        company: 'OpenAI',
        location: 'San Francisco, CA',
        type: 'Full-time',
        salary: '$180,000 - $250,000',
        posted: '1 day ago',
        description: 'Join our team to build cutting-edge AI systems that will shape the future of technology.',
        skills: ['Python', 'TensorFlow', 'PyTorch', 'Machine Learning', 'Deep Learning'],
        matchScore: 88,
        isLiked: true,
        applyLink: 'https://openai.com/careers/123456',
      },
      {
        id: '3',
        title: 'Frontend Developer',
        company: 'Stripe',
        location: 'Remote',
        type: 'Full-time',
        salary: '$120,000 - $160,000',
        posted: '3 days ago',
        description: 'Help us build beautiful, intuitive interfaces that make online payments seamless.',
        skills: ['React', 'JavaScript', 'CSS', 'TypeScript', 'Design Systems'],
        matchScore: 92,
        isLiked: false,
        applyLink: 'https://stripe.com/jobs/123456',
      },
      {
        id: '4',
        title: 'Data Scientist',
        company: 'Netflix',
        location: 'Los Gatos, CA',
        type: 'Full-time',
        salary: '$140,000 - $190,000',
        posted: '5 days ago',
        description: 'Use data to help us understand our users and improve our content recommendations.',
        skills: ['Python', 'SQL', 'Machine Learning', 'Statistics', 'Big Data'],
        matchScore: 85,
        isLiked: false,
        applyLink: 'https://jobs.netflix.com/123456',
      },
      {
        id: '5',
        title: 'Product Manager',
        company: 'Figma',
        location: 'San Francisco, CA',
        type: 'Full-time',
        salary: '$130,000 - $180,000',
        posted: '1 week ago',
        description: 'Lead product strategy and work with engineering teams to build amazing design tools.',
        skills: ['Product Management', 'Strategy', 'User Research', 'Analytics', 'Leadership'],
        matchScore: 78,
        isLiked: true,
        applyLink: 'https://figma.com/careers/123456',
      },
      {
        id: '6',
        title: 'DevOps Engineer',
        company: 'Docker',
        location: 'Austin, TX',
        type: 'Full-time',
        salary: '$110,000 - $150,000',
        posted: '4 days ago',
        description: 'Help us scale our infrastructure and build tools that make development easier.',
        skills: ['Docker', 'Kubernetes', 'AWS', 'CI/CD', 'Infrastructure'],
        matchScore: 82,
        isLiked: false,
        applyLink: 'https://docker.com/careers/123456',
      },
    ];

    setTimeout(() => {
      setJobs(mockJobs);
      setFilteredJobs(mockJobs);
      setLoading(false);
    }, 1000);
  }, []);

  const jobTypes = ['Full-time', 'Part-time', 'Contract', 'Internship'];
  const locations = ['Remote', 'San Francisco, CA', 'New York, NY', 'Mountain View, CA', 'Austin, TX'];
  const salaryRanges = ['$50k+', '$100k+', '$150k+', '$200k+'];

  const handleSearch = (term: string) => {
    setSearchTerm(term);
    filterJobs(term, selectedFilters);
  };

  const handleFilterChange = (key: string, value: string | string[]) => {
    const newFilters = { ...selectedFilters, [key]: value };
    setSelectedFilters(newFilters);
    filterJobs(searchTerm, newFilters);
  };

  const filterJobs = (search: string, filters: typeof selectedFilters) => {
    let filtered = jobs.filter(job => {
      const matchesSearch = job.title.toLowerCase().includes(search.toLowerCase()) ||
                          job.company.toLowerCase().includes(search.toLowerCase()) ||
                          job.skills.some(skill => skill.toLowerCase().includes(search.toLowerCase()));
      
      const matchesType = !filters.type || job.type === filters.type;
      const matchesLocation = !filters.location || job.location.includes(filters.location);
      const matchesSalary = !filters.salary || (job.salary && job.salary.includes(filters.salary));
      const matchesSkills = filters.skills.length === 0 || 
                           filters.skills.some(skill => job.skills.includes(skill));

      return matchesSearch && matchesType && matchesLocation && matchesSalary && matchesSkills;
    });

    // Sort jobs
    switch (sortBy) {
      case 'relevance':
        filtered.sort((a, b) => b.matchScore - a.matchScore);
        break;
      case 'date':
        filtered.sort((a, b) => new Date(b.posted).getTime() - new Date(a.posted).getTime());
        break;
      case 'salary':
        filtered.sort((a, b) => {
          const aSalary = parseInt(a.salary?.replace(/[^0-9]/g, '') || '0');
          const bSalary = parseInt(b.salary?.replace(/[^0-9]/g, '') || '0');
          return bSalary - aSalary;
        });
        break;
    }

    setFilteredJobs(filtered);
  };

  const toggleLike = (jobId: string) => {
    setJobs(prev => prev.map(job => 
      job.id === jobId ? { ...job, isLiked: !job.isLiked } : job
    ));
    setFilteredJobs(prev => prev.map(job => 
      job.id === jobId ? { ...job, isLiked: !job.isLiked } : job
    ));
  };

  const getMatchColor = (score: number) => {
    if (score >= 90) return 'text-green-400';
    if (score >= 80) return 'text-yellow-400';
    if (score >= 70) return 'text-orange-400';
    return 'text-red-400';
  };

  const getMatchBg = (score: number) => {
    if (score >= 90) return 'bg-green-500/20';
    if (score >= 80) return 'bg-yellow-500/20';
    if (score >= 70) return 'bg-orange-500/20';
    return 'bg-red-500/20';
  };

  if (loading) {
    return (
      <div className="min-h-screen pt-16 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-white/20 border-t-blue-500 rounded-full animate-spin mx-auto mb-4" />
          <p className="text-white/70">Loading jobs...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen pt-36 pb-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-4">
            Find Your <span className="gradient-text">Dream Job</span>
          </h1>
          <p className="text-xl text-white/70">
            Discover opportunities that match your skills and aspirations
          </p>
        </div>

        {/* Search and Filters */}
        <div className="mb-8">
          <div className="flex flex-col lg:flex-row gap-4 mb-6">
            <div className="flex-1">
              <Input
                placeholder="Search jobs, companies, or skills..."
                value={searchTerm}
                onChange={(e) => handleSearch(e.target.value)}
                icon={<Search size={20} />}
                variant="glass"
              />
            </div>
            <Button
              variant="glass"
              onClick={() => setShowFilters(!showFilters)}
              icon={<Filter size={20} />}
            >
              Filters
            </Button>
          </div>

          {/* Filter Panel */}
          <AnimatePresence>
            {showFilters && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="mb-6"
              >
                <Card variant="glass" className="p-6">
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-white/90 mb-2">
                        Job Type
                      </label>
                      <select
                        value={selectedFilters.type}
                        onChange={(e) => handleFilterChange('type', e.target.value)}
                        className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                      >
                        <option value="">All Types</option>
                        {jobTypes.map(type => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-white/90 mb-2">
                        Location
                      </label>
                      <select
                        value={selectedFilters.location}
                        onChange={(e) => handleFilterChange('location', e.target.value)}
                        className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                      >
                        <option value="">All Locations</option>
                        {locations.map(location => (
                          <option key={location} value={location}>{location}</option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-white/90 mb-2">
                        Salary Range
                      </label>
                      <select
                        value={selectedFilters.salary}
                        onChange={(e) => handleFilterChange('salary', e.target.value)}
                        className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                      >
                        <option value="">All Salaries</option>
                        {salaryRanges.map(range => (
                          <option key={range} value={range}>{range}</option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-white/90 mb-2">
                        Sort By
                      </label>
                      <select
                        value={sortBy}
                        onChange={(e) => setSortBy(e.target.value)}
                        className="w-full px-3 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:border-blue-400"
                      >
                        <option value="relevance">Relevance</option>
                        <option value="date">Date Posted</option>
                        <option value="salary">Salary</option>
                      </select>
                    </div>
                  </div>
                </Card>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Results Count */}
        <div className="flex items-center justify-between mb-6">
          <p className="text-white/70">
            {filteredJobs.length} job{filteredJobs.length !== 1 ? 's' : ''} found
          </p>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2 text-white/70">
              <TrendingUp size={16} />
              <span>High Match</span>
            </div>
            <div className="flex items-center space-x-2 text-white/70">
              <Users size={16} />
              <span>Popular</span>
            </div>
          </div>
        </div>

        {/* Job Listings */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Job Cards */}
          <div className="lg:col-span-2 space-y-4">
            <AnimatePresence>
              {filteredJobs.map((job, index) => (
                <motion.div
                  key={job.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                >
                  <Card variant="glass" className="p-6 hover:scale-[1.02] transition-all duration-300">
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex-1">
                        <div className="flex items-center space-x-3 mb-2">
                          <h3 className="text-xl font-semibold text-white">
                            {job.title}
                          </h3>
                          <div className={`px-2 py-1 rounded-full text-xs font-medium ${getMatchBg(job.matchScore)} ${getMatchColor(job.matchScore)}`}>
                            {job.matchScore}% match
                          </div>
                        </div>
                        <p className="text-white/70 text-lg mb-2">
                          {job.company}
                        </p>
                        <div className="flex items-center space-x-4 text-white/60 text-sm">
                          <div className="flex items-center space-x-1">
                            <MapPin size={16} />
                            <span>{job.location}</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Clock size={16} />
                            <span>{job.posted}</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Briefcase size={16} />
                            <span>{job.type}</span>
                          </div>
                        </div>
                      </div>
                      <button
                        onClick={() => toggleLike(job.id)}
                        className={`p-2 rounded-lg transition-colors duration-300 ${
                          job.isLiked 
                            ? 'text-red-400 bg-red-500/20' 
                            : 'text-white/50 hover:text-red-400 hover:bg-red-500/20'
                        }`}
                      >
                        <Heart size={20} fill={job.isLiked ? 'currentColor' : 'none'} />
                      </button>
                    </div>

                    <p className="text-white/80 mb-4 line-clamp-2">
                      {job.description}
                    </p>

                    <div className="flex flex-wrap gap-2 mb-4">
                      {job.skills.slice(0, 4).map((skill) => (
                        <span
                          key={skill}
                          className="px-3 py-1 bg-white/10 text-white/70 text-sm rounded-lg"
                        >
                          {skill}
                        </span>
                      ))}
                      {job.skills.length > 4 && (
                        <span className="px-3 py-1 bg-white/10 text-white/70 text-sm rounded-lg">
                          +{job.skills.length - 4} more
                        </span>
                      )}
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        {job.salary && (
                          <div className="flex items-center space-x-1 text-green-400">
                            <DollarSign size={16} />
                            <span className="font-medium">{job.salary}</span>
                          </div>
                        )}
                      </div>
                      <div className="flex items-center space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => window.open(job.applyLink, '_blank')}
                          icon={<ExternalLink size={16} />}
                        >
                          Apply
                        </Button>
                      </div>
                    </div>
                  </Card>
                </motion.div>
              ))}
            </AnimatePresence>

            {filteredJobs.length === 0 && (
              <Card variant="glass" className="p-12 text-center">
                <div className="w-16 h-16 bg-white/10 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <Search size={32} className="text-white/50" />
                </div>
                <h3 className="text-xl font-semibold text-white mb-2">
                  No jobs found
                </h3>
                <p className="text-white/70">
                  Try adjusting your search criteria or filters
                </p>
              </Card>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Quick Stats */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Your Job Search
              </h3>
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Jobs Applied</span>
                  <span className="text-white font-semibold">12</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Interviews</span>
                  <span className="text-white font-semibold">3</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-white/70">Saved Jobs</span>
                  <span className="text-white font-semibold">
                    {jobs.filter(job => job.isLiked).length}
                  </span>
                </div>
              </div>
            </Card>

            {/* Top Skills */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Trending Skills
              </h3>
              <div className="space-y-2">
                {['React', 'Python', 'Machine Learning', 'AWS', 'TypeScript'].map((skill, index) => (
                  <div key={skill} className="flex items-center justify-between">
                    <span className="text-white/70">{skill}</span>
                    <div className="flex items-center space-x-2">
                      <div className="w-16 h-2 bg-white/10 rounded-full overflow-hidden">
                        <div 
                          className="h-full bg-gradient-to-r from-horizon-800 to-horizon-900 rounded-full"
                          style={{ width: `${90 - index * 10}%` }}
                        />
                      </div>
                      <span className="text-white/50 text-sm">{90 - index * 10}%</span>
                    </div>
                  </div>
                ))}
              </div>
            </Card>

            {/* Job Alerts */}
            <Card variant="glass" className="p-6">
              <h3 className="text-lg font-semibold text-white mb-4">
                Job Alerts
              </h3>
              <p className="text-white/70 text-sm mb-4">
                Get notified when new jobs match your criteria
              </p>
              <Button variant="glass" className="w-full">
                Create Alert
              </Button>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobListingsPage;
